import React, { useState } from 'react';

function App() {
    const [selectedFiles, setSelectedFiles] = useState(null);
    const [techStack, setTechStack] = useState("python:3.8");
    const [requirementsFile, setRequirementsFile] = useState(null); // Новое состояние для загрузки requirements.txt

    const handleFileChange = (e) => {
        setSelectedFiles(e.target.files);
    };

    const handleRequirementsFileChange = (e) => {
        setRequirementsFile(e.target.files[0]); // Загружаем файл requirements.txt
    };

    const handleSubmit = async () => {
        if (!selectedFiles) {
            alert("Пожалуйста, выберите файл!");
            return;
        }

        const formData = new FormData();
        formData.append("project_files", selectedFiles[0]);  // Обработка выбранного файла
        formData.append("tech_stack", techStack);            // Передача стека технологий

        if (requirementsFile) {
            formData.append("user_requirements", requirementsFile);  // Передача файла requirements.txt
        }

        try {
            const response = await fetch("http://127.0.0.1:8000/create_docker_image/", {
                method: "POST",
                body: formData
            });

            const result = await response.json();
            console.log(result);
            alert("Docker образ успешно создан и сохранен!");
        } catch (error) {
            console.error("Error:", error);
            alert("Ошибка при создании Docker образа!");
        }
    };

    return (
        <div>
            <h1>Docker Image Generator</h1>
            <div>
                <label>Загрузите ваш проект:</label>
                <input type="file" onChange={handleFileChange} />
            </div>
            <div>
                <label>Выберите стек технологий (например, Python 3.8):</label>
                <select onChange={(e) => setTechStack(e.target.value)}>
                    <option value="python:3.8">Python 3.8</option>
                    <option value="node:14">Node 14</option>
                    <option value="python:3.9">Python 3.9</option>
                </select>
            </div>
            <div>
                <label>Загрузите файл requirements.txt (если есть):</label>
                <input type="file" onChange={handleRequirementsFileChange} />
            </div>
            <button onClick={handleSubmit}>Создать Docker Image</button>
        </div>
    );
}

export default App;
