import React, { useState } from 'react';

function App() {
    const [selectedFiles, setSelectedFiles] = useState(null);
    const [techStack, setTechStack] = useState("python:3.8");

    const handleFileChange = (e) => {
        setSelectedFiles(e.target.files);
    };

    const handleSubmit = async () => {
        const formData = new FormData();
        formData.append("project_files", selectedFiles[0]);
        formData.append("tech_stack", techStack);

        const response = await fetch("http://127.0.0.1:8000/create_docker_image/", {
            method: "POST",
            body: formData
        });

        const result = await response.json();
        console.log(result);
    };

    return (
        <div>
            <h1>Docker Image Generator</h1>
            <input type="file" onChange={handleFileChange} />
            <select onChange={(e) => setTechStack(e.target.value)}>
                <option value="python:3.8">Python 3.8</option>
                <option value="node:14">Node 14</option>
            </select>
            <button onClick={handleSubmit}>Create Docker Image</button>
        </div>
    );
}

export default App;
